mapper.select:
SELECT dish.id, dish.name, category_id, price, code, image, description, status, dish.sort, dish.create_time AS create_time, dish.update_time AS update_time, dish.create_user AS create_user, dish.update_user AS update_user, category.name AS categoryName, dish.is_deleted FROM dish,category WHERE dish.category_id = category.id AND dish.is_deleted = 0
AND dish.name LIKE #{name}
LIMIT #{page}, #{pageSize}
